firestore.rules:
rules_version = '2';

service cloud.firestore {
  match /databases/{database}/documents {

    // == Helper Functions ==
    function isSuperAdmin() {
      return request.auth.uid == 'ttFbsVWt14cdTwVlKs1AbgBLUtx1';
    }

    function isAuth() {
      return request.auth != null;
    }

    function isOwner(userId) {
      return isAuth() && request.auth.uid == userId;
    }

    function isTenantOwner(tenantId) {
      return isAuth() && get(/databases/$(database)/documents/users/$(request.auth.uid)).data.tenantId == tenantId;
    }

    function isCorrectToken(tenantId) {
        let tenantData = get(/databases/$(database)/documents/tenants/$(tenantId)).data;
        return request.resource.data.verificationToken == tenantData.tokenHarian;
    }

    // == Global Rule: Super Admin has full read/write access to everything. ==
    match /{path=**} {
      allow read, write: if isSuperAdmin();
    }

    // == Collection: users ==
    match /users/{userId} {
      allow get: if isOwner(userId);
      allow list: if isSuperAdmin();
    }

    // == Collection: tenants ==
    match /tenants/{tenantId} {
      // Anyone can read the public profile of a cafe, and list all cafes.
      allow get, list: if true;

      // == Sub-collections within a Tenant ==

      // --- PUBLIC-READ collections ---
      // Anyone can read menus, categories, and tables.
      match /menus/{docId} { allow read: if true; }
      match /categories/{docId} { allow read: if true; }
      match /tables/{docId} { allow read: if true; }

      // --- PROTECTED-WRITE sub-collections ---
      // Only tenant owners can write to their own sub-collections.
      match /menus/{docId} { allow write: if isTenantOwner(tenantId); }
      match /categories/{docId} { allow write: if isTenantOwner(tenantId); }
      match /tables/{docId} { allow write: if isTenantOwner(tenantId); }

      // --- SPECIAL-CASE: Orders ---
      match /orders/{orderId} {
        // ANYONE can create an order IF they provide the correct daily token.
        allow create: if isCorrectToken(tenantId);
        // Tenant owners can read/write all orders in their tenant.
        allow read, update, delete: if isTenantOwner(tenantId);
      }
    }
  }
}
